import './App.css';
import QRCode from 'react-qr-code';

function App() {
  const [text, setText] = useState("");

  function generateQR() {
    // No need to pass any arguments to this function
    // The value of the input field is already stored in the 'text' state
  }

  function handleChange(e) {
    setText(document.getElementById("input-text").value);
  }

  return (
    <div className="App">
      <h1>QR Code Generator</h1>
      <QRCode id='qr' value={text} />

      <div className='input-here' >
        <p>Enter your site here</p>
        <input
          type="text"
          value={text}
          id="input-text"
           // Use 'onChange' to update the 'text' state
        />
        <button onClick={() => handleChange()}>Generate</button>
      </div>
    </div>
  );
}
