import { useState } from 'react';
import './App.css';
import QRCode from 'react-qr-code';

function App() {
  const [text, setText] = useState("");

  function generateQR(e){
    setText(e.target.value);
  }

  function handleChange(e){
    setText(e.target.value)
  }

  return (
    <div className="App">
    <h1>QR Code Generator</h1>
    <QRCode id='qr' value={text} />

    <div className='input-here' >
      <p>Enter your site here</p>
      <input type="text" value={text} id="input-text"  />
      <button onClick={(e)=>{ handleChange(e)}} >Generate</button>
    </div>
    </div>
  );
}

export default App;
