import { useState, useRef } from 'react';
import './App.css';
import QRCode from 'react-qr-code';
import html2canvas from 'html2canvas'; 

function App() {
  const [text, setText] = useState("");
  const qrCodeRef = useRef(null);

  function handleChange(e){
    setText(e.target.value)
  }

  function handleDownload() {
    html2canvas(qrCodeRef.current) // Capture the QR code element
      .then(canvas => {
        const link = document.createElement('a');
        link.download = 'qrcode.png'; // Set the image filename
        link.href = canvas.toDataURL('image/png'); // Convert canvas to data URL
        link.click(); // Trigger the download
      });
  }

  return (
    <div className="App">
    <h1>QR Code Generator</h1>
    <QRCode id='qr' value={text} ref={qrCodeRef}  />

    <div className='input-here' >
      <p>Enter your site here</p>
      <input type="text" value={text} id="input-text" onChange={(e)=>{ handleChange(e)}}  />
      <button >Generate</button>
      <button onClick={() => handleDownload()}>Download QR Code</button>
    </div>
    </div>
  );
}

export default App;
